$('.photos').click( function() {
    var $this = $(this);
    $this.css('z-index', 4)
    .siblings('.photos').removeClass('expanded').css('z-index', 1);
    $this.animate({
        left: 0, top: 0, margin: 0, 
        width: '100vw', height: '100vh',
        'border-radius': 0, padding: '60px 5px 5px 5px'
    }, 1000).addClass('expanded');
    $this.siblings('.photos').animate({
        left: 0, top: 0, height: 0, width: 50,
        'border-radius': 25, padding: '0'
    }).first().css({ 'margin': '5px' })
      .next().css({ 'margin': '5px 5px 5px 5px' });
    $this.css('z-index', 0);
});